import axios from 'axios';
import { useEffect, useState } from 'react';

export default function BookList({ refreshTrigger }) {
  const [books, setBooks] = useState([]);

  const fetchBooks = () => {
    axios.get('http://localhost:3000/books')
      .then(res => setBooks(res.data))
      .catch(err => console.error(err));
  };
  useEffect(() => {
    fetchBooks();
  }, [refreshTrigger]);

  const handleDelete = (id) => {
    axios.delete(`http://localhost:3000/books/${id}`)
      .then(() => fetchBooks())
      .catch(err => console.error(err));
  };
  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">ðŸ“š Lista de libros</h2>
      <ul className="space-y-2">
        {books.map(book => (
          <li key={book.id} className="border p-3 rounded shadow flex justify-between items-center">
            <div>
              <strong>{book.title}</strong> â€” {book.author} ({book.published_year}) [{book.genre}]
            </div>
            <button
              onClick={() => handleDelete(book.id)}
              className="ml-4 px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700"
            >
              Eliminar
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
